import React from 'react'
import Rating from './Rating';

export default function Product(props) {
    console.log('Product', props.product);
    const {product} =  props;
    return (
        <div>
            <div class="card">
  <img src= {product.image} class="card-img-top" alt={product.name}/>
  <div class="card-body">
    <h5 class="card-title">{product.name}</h5>
    <p><Rating value = {product.rating} text={`${product.numReviews} reviews`}/></p>
    <p class="card-text">{product.description}</p>
    <p class="card-text">{product.rating} from {product.numReviews} reviews</p>
    <h4 class="card-text">${product.price}</h4>
    <a href={`/product/${product._id}`} class="btn btn-primary">Add to cart</a>
  </div>
</div>
        </div>
    )
}
