import { USER_DETAIL_FAIL, USER_DETAIL_REQUEST, USER_DETAIL_SUCCESS, USER_LOGIN_FAIL, USER_LOGIN_REQUEST, USER_LOGIN_SUCCESS, USER_LOGOUT, USER_REGISTER_FAIL, USER_REGISTER_REQUEST, USER_REGISTER_SUCCESS } from "../constants/constants"



export const userReducer = (state = {}, action) => {
switch(action.type) {
    case USER_LOGIN_REQUEST: 
        return {loading: true }
    case USER_LOGIN_SUCCESS: 
        return {loading: false, userInfo: action.payload}
    case USER_LOGIN_FAIL: 
        return {loading: false, error: action.payload}

    case USER_REGISTER_REQUEST: 
        return {loading: true }
    case USER_REGISTER_SUCCESS: 
        return {loading: false, userInfo: action.payload}
    case USER_REGISTER_FAIL: 
        return {loading: false, error: action.payload}
    case USER_LOGOUT: 
    return {}

    case USER_DETAIL_REQUEST: 
    return {loading: true }
    case USER_DETAIL_SUCCESS: 
    return {loading: false, user: action.payload}
    case USER_DETAIL_FAIL: 
    return {loading: false, error: action.payload}


    default:
    return state
}

}

export const userInfoReducer = (state = {user: {}}, action) => {
    switch(action.type) {
        case USER_DETAIL_REQUEST: 
        return {loading: true }
        case USER_DETAIL_SUCCESS: 
        return {loading: false, user: action.payload}
        case USER_DETAIL_FAIL: 
        return {loading: false, error: action.payload}


        default:
        return state
    }

    }
