import { PRODUCT_LIST_FAIL, PRODUCT_LIST_REQUEST, PRODUCT_LIST_SUCCESS,
    PRODUCT_DETAIL_REQUEST, PRODUCT_DETAIL_SUCCESS, PRODUCT_DETAIL_FAIL, PRODUCT_UPDATE_REQUEST, PRODUCT_UPDATE_SUCCESS, PRODUCT_UPDATE_FAIL, PRODUCT_UPDATE_RESET
 } from "../constants/constants"


export const productReducer = (state = {products: [], product: {}}, action) => {
switch(action.type) {
    case PRODUCT_LIST_REQUEST: 
        return {loading: true, products: [] }
    case PRODUCT_LIST_SUCCESS: 
        return {loading: false, products: action.payload}
    case PRODUCT_LIST_FAIL: 
        return {loading: false, error: action.payload}
    case PRODUCT_DETAIL_REQUEST: 
        return {loading: true, product: {} }
    case PRODUCT_DETAIL_SUCCESS: 
        return {loading: false, product: action.payload}
    case PRODUCT_DETAIL_FAIL: 
        return {loading: false, error: action.payload}
    default:
    return state
}


}

export const productUpdateReducer = (state = { product: {} }, action) => {
    switch (action.type) {
      case PRODUCT_UPDATE_REQUEST:
        return { loading: true }
      case PRODUCT_UPDATE_SUCCESS:
        return { loading: false, success: true, product: action.payload }
      case PRODUCT_UPDATE_FAIL:
        return { loading: false, error: action.payload }
      case PRODUCT_UPDATE_RESET:
        return { product: {} }
      default:
        return state
    }
  }
