import { ORDER_CREATE_SUCCESS, ORDER_CREATE_REQUEST, ORDER_CREATE_FAIL, ORDER_DETAIL_REQUEST, ORDER_DETAIL_SUCCESS, ORDER_DETAIL_FAIL } from "../constants/constants";



export const orderCreateReducer = (state= {}, action) => {
    switch(action.type) {
        case ORDER_CREATE_REQUEST: 
        return {loading: true}
        case ORDER_CREATE_SUCCESS: 
        return {loading: false, success: true, order: action.payload}
        case ORDER_CREATE_FAIL: 
        return {loading: false }
        default: 
        return state
    }

}

export const orderDetailReducer = (state= {orderItems: [], shippingAddress: {}}, action) => {
    switch(action.type) {
        case ORDER_DETAIL_REQUEST: 
        return {loading: true}
        case ORDER_DETAIL_SUCCESS: 
        return {loading: false, success: true, order: action.payload}
        case ORDER_DETAIL_FAIL: 
        return {loading: false }
        default: 
        return state
    }

}
